import React, { useEffect } from 'react'
import { IState } from '../../store'
import { actions } from './reducer'
import { useSelector, useDispatch } from 'react-redux'
import { useQuery } from 'urql'
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Label, ResponsiveContainer } from 'recharts'
import { LinearProgress } from '@material-ui/core'

const MEASUREMENTS_QUERY = `
   query($input: MeasurementQuery){
       getMeasurements(input: $input){
           metric
           at
           value
           unit
       }
   }
`

const getLastKnownMeasurement = (state: IState) => {
        const lastMeasurement = state.metric.lastMeasurement
        return { lastMeasurement }
    }

const getMeasurements = ( state: IState ) => {
    const Measurements = state.metric.Measurements
    return { Measurements }
}

const ShowChartData = () => {

    const { lastMeasurement } = useSelector(getLastKnownMeasurement)
    const  Measurements  = useSelector(getMeasurements)
    const timeLimit = new Date().getTime()
    const input = {
        metricName: lastMeasurement.metric,
        after: lastMeasurement.at,
        before: timeLimit - 45,
    }

    const [result] = useQuery({
        query: MEASUREMENTS_QUERY,
        variables: {
            input,
        },
        pause: !lastMeasurement.metric,
        // pollInterval: 5000,
        // requestPolicy: 'cache-and-network',
    })

    const dispatch = useDispatch()

    const { fetching, data, error } = result
    useEffect(() => {
        if(error){
            console.log(error)
        }
        if (!data) return
        const measurements = data
        dispatch(actions.chartMeasurements({ getMeasurements: measurements }))
    }, [dispatch, data, error])


    const testConsole = () => {
        if(!data) {
            return <LinearProgress />
        } else {
            console.log(data.getMeasurements)
        }

        // Measurements.map((measurement: any) => {
        //     return(
        //         console.log(typeof measurement.value)
        //     )
        // })
    }

    return(
        <div>
            {testConsole()}
            <ResponsiveContainer width={700} height="80%">
            {/* <LineChart width={1920} height={1080} data={Measurements} margin={{bottom: 10}}>
                <Line type="monotone" dataKey="value" stroke="#8884d8"/>

            </LineChart> */}
            </ResponsiveContainer>
        </div>
    )
}

export default () => {
    return(
        <ShowChartData />
    )
}
