import React, { useState } from 'react'
import { IState } from '../../store'
import { useSelector } from 'react-redux'
import { Line } from 'recharts'

const getLastKnownMeasurement = (state: IState) => {
    const lastMeasurement = state.metric.lastMeasurement
    return { lastMeasurement }
}


const ShowMultiple = () => {

    const { lastMeasurement } = useSelector(getLastKnownMeasurement); // array of user selected metrics
    const [ metricsArray, setMetricsArray ] = useState([] as any)

    const addToMetricsArray = () => {
        metricsArray.push(lastMeasurement)
    }

    const removeDuplicatesFromArray = (metricsArray: []) => {
        let filteredArray = (metricsArray: []) => metricsArray.filter((metric: any) => metric.metric === lastMeasurement.metric)
    }

    // const showMultiLines = () => {
    //     return selectedMetrics.map(metric => {
    //         return(
    //             <div>
    //                 {/* <Line datakey="value">

    //                 </Line> */}
    //             </div>
    //         )
    //     })


    // console.log(lastMeasurement)

    return(
        <div>
            {void addToMetricsArray()}
            {removeDuplicatesFromArray(metricsArray)}
        </div>
    )
}

export default ShowMultiple;
